import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Grid, 
  Container, 
  Card, 
  CardContent, 
  Avatar, 
  LinearProgress,
  TextField,
  Button
} from '@mui/material';
import { 
  WbSunny as SunIcon, 
  Cloud as CloudIcon, 
  AcUnit as SnowIcon, 
  WaterDropOutlined as RainIcon,
  DeviceThermostatOutlined as TemperatureIcon,
  LocationOn as LocationIcon
} from '@mui/icons-material';

// Кастомні кольори в стилі Soft UI
const softUIPalette = {
  primary: '#7928CA',   // Яскравий фіолетовий
  secondary: '#FF0080', // Рожевий
  background: '#F8F9FA',
  cardBackground: 'white',
  gradientPrimary: 'linear-gradient(to right, #7928CA, #FF0080)',
  gradientSecondary: 'linear-gradient(to right, #1A73E8, #1192EC)'
};

function Dashboard() {
  const [city, setCity] = useState('Київ');
  const [searchCity, setSearchCity] = useState('Київ');
  const [weatherData, setWeatherData] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);

  // Форматування дати з повною назвою дня, числом та місяцем
  const formatFullDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('uk-UA', {
      weekday: 'long',
      day: 'numeric',
      month: 'long'
    });
  };

  const mapConditionToIcon = (condition) => {
    switch(condition) {
      case 'sunny': return <SunIcon fontSize="large" />;
      case 'cloudy': return <CloudIcon fontSize="large" />;
      case 'snow': return <SnowIcon fontSize="large" />;
      case 'rain': return <RainIcon fontSize="large" />;
      default: return <CloudIcon fontSize="large" />;
    }
  };

  const fetchWeatherData = async () => {
    setLoading(true);
    try {
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${searchCity}`);
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${searchCity}`);
      
      const weatherData = await weatherResponse.json();
      const forecastData = await forecastResponse.json();
      
      setWeatherData(weatherData);
      setForecast(forecastData.map(day => ({
        ...day,
        fullDate: formatFullDate(day.date),
        icon: mapConditionToIcon(day.condition)
      })));
      setLoading(false);
    } catch (error) {
      console.error('Помилка отримання погоди:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWeatherData();
  }, []);

  const WeatherCard = ({ title, value, icon, gradient }) => (
    <Card 
      sx={{ 
        background: gradient || softUIPalette.gradientPrimary,
        color: 'white',
        borderRadius: 3,
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
      }}
    >
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Box>
            <Typography variant="h6" sx={{ opacity: 0.7 }}>{title}</Typography>
            <Typography variant="h4">{value}</Typography>
          </Box>
          {icon}
        </Box>
      </CardContent>
    </Card>
  );

  const WeatherForecastItem = ({ fullDate, minTemp, maxTemp, icon }) => (
    <Card 
      sx={{ 
        textAlign: 'center', 
        borderRadius: 3,
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
        transition: 'transform 0.3s',
        '&:hover': { transform: 'scale(1.05)' }
      }}
    >
      <CardContent>
        <Typography variant="subtitle2" sx={{ fontWeight: 'bold' }}>{fullDate}</Typography>
        <Box my={2}>
          {icon}
        </Box>
        <Typography variant="body1">
          Мін. {minTemp}°C / Макс. {maxTemp}°C
        </Typography>
      </CardContent>
    </Card>
  );

  return (
    <Container maxWidth="xl" sx={{ backgroundColor: softUIPalette.background, minHeight: '100vh', py: 4 }}>
      <Grid container spacing={3}>
        {/* Пошук міста */}
        <Grid item xs={12}>
          <Card sx={{ borderRadius: 3, boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
            <CardContent>
              <Box display="flex" gap={2}>
                <TextField
                  fullWidth
                  label="Місто"
                  value={searchCity}
                  onChange={(e) => setSearchCity(e.target.value)}
                  variant="outlined"
                />
                <Button 
                  variant="contained" 
                  sx={{ 
                    background: softUIPalette.gradientPrimary,
                    color: 'white',
                    '&:hover': { background: softUIPalette.gradientSecondary }
                  }}
                  onClick={fetchWeatherData}
                >
                  Пошук
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {loading ? (
          <Grid item xs={12}>
            <LinearProgress color="secondary" />
          </Grid>
        ) : weatherData && (
          <>
            {/* Основна інформація про погоду */}
            <Grid item xs={12} md={4}>
              <WeatherCard
                title="Температура"
                value={`${weatherData.temperature}°C`}
                icon={<TemperatureIcon fontSize="large" />}
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <WeatherCard
                title="Вологість"
                value={`${weatherData.humidity}%`}
                gradient={softUIPalette.gradientSecondary}
                icon={<LocationIcon fontSize="large" />}
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <WeatherCard
                title="Вітер"
                value={`${weatherData.windSpeed} км/год`}
                icon={<CloudIcon fontSize="large" />}
              />
            </Grid>

            {/* Прогноз на наступні дні */}
            <Grid item xs={12}>
              <Typography variant="h5" sx={{ my: 2, fontWeight: 'bold' }}>
                Прогноз на тиждень
              </Typography>
              <Grid container spacing={2}>
                {forecast.slice(0, 7).map((day, index) => (
                  <Grid item xs={6} sm={4} md={2} key={index}>
                    <WeatherForecastItem
                      fullDate={day.fullDate}
                      minTemp={day.minTemperature}
                      maxTemp={day.maxTemperature}
                      icon={day.icon}
                    />
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </>
        )}
      </Grid>
    </Container>
  );
}

export default Dashboard;
