import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Typography, 
  Box,
  TextField,
  Button,
  CircularProgress,
  Card,
  CardContent,
  CardHeader,
  Avatar,
  IconButton,
  Chip
} from '@mui/material';
import { 
  LineChart, 
  Line, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend,
  BarChart,
  Bar
} from 'recharts';
import { 
  LocationOn as LocationIcon, 
  AutoGraph as GraphIcon,
  Refresh as RefreshIcon,
  WaterDropOutlined as WaterDropIcon,
  AirOutlined as WindIcon,
  DeviceThermostatOutlined as TemperatureIcon,
  CloudOutlined as CloudIcon,
  WbSunnyOutlined as SunIcon,
  AcUnitOutlined as SnowIcon,
  ThunderstormOutlined as ThunderIcon,
  WaterOutlined as RainIcon
} from '@mui/icons-material';

// Функція для вибору іконки погоди
const getWeatherIcon = (weatherCondition) => {
  // Перевірка на null або undefined
  if (!weatherCondition) {
    return <CloudIcon color="disabled" />;
  }

  switch (weatherCondition.toLowerCase()) {
    case 'clear': return <SunIcon color="warning" />;
    case 'clouds': return <CloudIcon color="disabled" />;
    case 'rain': return <RainIcon color="primary" />;
    case 'snow': return <SnowIcon color="info" />;
    case 'thunderstorm': return <ThunderIcon color="error" />;
    default: return <CloudIcon color="disabled" />;
  }
};

function Dashboard() {
  const [city, setCity] = useState('Київ');
  const [searchCity, setSearchCity] = useState('Київ');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    setLoading(true);
    setError('');
    
    try {
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${searchCity}`);
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${searchCity}`);
      
      if (!weatherResponse.ok || !forecastResponse.ok) {
        throw new Error('Помилка отримання даних');
      }
      
      const weatherData = await weatherResponse.json();
      const forecastData = await forecastResponse.json();
      
      setCurrentWeather(weatherData);
      setForecast(forecastData);
      setCity(searchCity);
      setLoading(false);
    } catch (err) {
      console.error('Помилка:', err);
      setError(err.message || 'Помилка отримання даних погоди');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWeatherData();
  }, []);

  return (
    <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        {/* Пошук міста */}
        <Grid item xs={12}>
          <Card elevation={6}>
            <CardContent>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <TextField
                  label="Введіть місто"
                  value={searchCity}
                  onChange={(e) => setSearchCity(e.target.value)}
                  fullWidth
                  variant="outlined"
                />
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={fetchWeatherData}
                  disabled={loading}
                >
                  {loading ? <CircularProgress size={24} /> : 'Пошук'}
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Поточна погода */}
        {currentWeather && (
          <>
            <Grid item xs={12} md={4}>
              <Card elevation={6}>
                <CardHeader title={`Поточна погода в ${city}`} />
                <CardContent>
                  <Typography variant="h4">
                    {currentWeather.temperature}°C
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
                    {getWeatherIcon(currentWeather.condition)}
                    <Typography sx={{ ml: 2 }}>
                      {currentWeather.condition}
                    </Typography>
                  </Box>
                  <Grid container sx={{ mt: 2 }}>
                    <Grid item xs={6}>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <WaterDropIcon color="primary" />
                        <Typography sx={{ ml: 1 }}>
                          Вологість: {currentWeather.humidity}%
                        </Typography>
                      </Box>
                    </Grid>
                    <Grid item xs={6}>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <WindIcon color="primary" />
                        <Typography sx={{ ml: 1 }}>
                          Вітер: {currentWeather.windSpeed} км/год
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>

            {/* Графік температури */}
            <Grid item xs={12} md={8}>
              <Card elevation={6}>
                <CardHeader title="Графік температури" />
                <CardContent>
                  <LineChart
                    width={700}
                    height={300}
                    data={forecast}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="temperature" stroke="#8884d8" />
                  </LineChart>
                </CardContent>
              </Card>
            </Grid>

            {/* Прогноз на 7 днів */}
            <Grid item xs={12}>
              <Card elevation={6}>
                <CardHeader title="Прогноз на 7 днів" />
                <CardContent>
                  <Grid container spacing={2}>
                    {forecast.slice(0, 7).map((day, index) => (
                      <Grid item xs={12} sm={4} md={2} key={index}>
                        <Card variant="outlined">
                          <CardContent>
                            <Typography variant="h6">
                              {new Date(day.date).toLocaleDateString('uk-UA', { weekday: 'short' })}
                            </Typography>
                            <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
                              {getWeatherIcon(day.condition)}
                            </Box>
                            <Typography variant="body2" align="center">
                              {day.temperature}°C
                            </Typography>
                            <Typography variant="caption" align="center" display="block">
                              {day.condition}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </CardContent>
              </Card>
            </Grid>

            {/* Додаткові графіки */}
            <Grid item xs={12} md={6}>
              <Card elevation={6}>
                <CardHeader title="Опади протягом тижня" />
                <CardContent>
                  <BarChart width={600} height={300} data={forecast}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="precipitation" fill="#8884d8" />
                  </BarChart>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card elevation={6}>
                <CardHeader title="Вологість повітря" />
                <CardContent>
                  <LineChart width={600} height={300} data={forecast}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="humidity" stroke="#82ca9d" />
                  </LineChart>
                </CardContent>
              </Card>
            </Grid>
          </>
        )}
      </Grid>
    </Container>
  );
}

export default Dashboard;
