import React, { useState } from 'react';
import { 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  TextField,
  Button,
  Box,
  CircularProgress
} from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

function WeatherAnalysis() {
  const [city, setCity] = useState('');
  const [originalCityName, setOriginalCityName] = useState(''); 
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchWeatherData = async () => {
    if (!city) return;
    
    setOriginalCityName(city);
    setLoading(true);
    setError('');
    
    try {
      // Отримуємо поточну погоду
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${city}`);
      
      if (!weatherResponse.ok) {
        const errorData = await weatherResponse.json();
        throw new Error(errorData.error || 'Помилка отримання даних про погоду');
      }
      
      const weatherData = await weatherResponse.json();
      
      // Отримуємо прогноз
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${city}`);
      
      if (!forecastResponse.ok) {
        const errorData = await forecastResponse.json();
        throw new Error(errorData.error || 'Помилка отримання прогнозу погоди');
      }
      
      const forecastData = await forecastResponse.json();
      
      // Створюємо копію даних про погоду, щоб замінити назву міста
      const modifiedWeatherData = {
        ...weatherData,
        originalCityName: originalCityName // Додаємо оригінальну назву міста
      };
      
      setCurrentWeather(modifiedWeatherData);
      setForecast(forecastData);
    } catch (err) {
      console.error('Помилка:', err);
      setError(err.message || 'Помилка отримання даних погоди');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', gap: 2 }}>
            <TextField
              label="Введіть місто"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              fullWidth
            />
            <Button 
              variant="contained" 
              onClick={fetchWeatherData}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Отримати погоду'}
            </Button>
          </Paper>
        </Grid>

        {error && (
          <Grid item xs={12}>
            <Paper sx={{ p: 2, bgcolor: 'error.light' }}>
              <Typography color="error">{error}</Typography>
            </Paper>
          </Grid>
        )}

        {currentWeather && (
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Поточна погода в {originalCityName}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                {currentWeather.icon && (
                  <img 
                    src={currentWeather.icon} 
                    alt={currentWeather.description}
                    style={{ marginRight: '10px' }}
                  />
                )}
                <Typography variant="h5">{currentWeather.temperature}°C</Typography>
              </Box>
              <Typography>Опис: {currentWeather.description}</Typography>
              <Typography>Вологість: {currentWeather.humidity}%</Typography>
              <Typography>Швидкість вітру: {currentWeather.windSpeed} км/год</Typography>
              {currentWeather.city !== originalCityName && (
                <Typography variant="caption" color="text.secondary">
                  Англійська назва: {currentWeather.city}
                </Typography>
              )}
            </Paper>
          </Grid>
        )}

        {forecast.length > 0 && (
          <Grid item xs={12}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                Прогноз погоди для {originalCityName}
              </Typography>
              <Box sx={{ width: '100%', height: 300, overflowX: 'auto' }}>
                <LineChart
                  width={800}
                  height={300}
                  data={forecast}
                  margin={{
                    top: 5,
                    right: 30,
                    left: 20,
                    bottom: 5,
                  }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis 
                    dataKey="date" 
                    tickFormatter={(date) => {
                      if (date instanceof Date) {
                        return date.toLocaleDateString();
                      }
                      return new Date(date).toLocaleDateString();
                    }}
                  />
                  <YAxis />
                  <Tooltip 
                    labelFormatter={(date) => {
                      if (date instanceof Date) {
                        return date.toLocaleDateString();
                      }
                      return new Date(date).toLocaleDateString();
                    }}
                  />
                  <Legend />
                  <Line 
                    type="monotone" 
                    dataKey="temperature" 
                    stroke="#8884d8" 
                    name="Температура"
                  />
                  <Line 
                    type="monotone" 
                    dataKey="humidity" 
                    stroke="#82ca9d" 
                    name="Вологість"
                  />
                </LineChart>
              </Box>
            </Paper>
          </Grid>
        )}
      </Grid>
    </Container>
  );
}

export default WeatherAnalysis;
