import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  Box,
  TextField,
  Button,
  CircularProgress
} from '@mui/material';
import { 
  LineChart, 
  Line, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend 
} from 'recharts';

function Dashboard() {
  const [city, setCity] = useState('');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // –ê–≥—Ä–æ–Ω–æ–º—ñ—á–Ω—ñ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ—ó –Ω–∞ –æ—Å–Ω–æ–≤—ñ –ø–æ–≥–æ–¥–Ω–∏—Ö –¥–∞–Ω–∏—Ö
  const getAgronomicRecommendations = (weather) => {
    if (!weather) return [];

    const recommendations = [];
    const temp = weather.temperature;
    const humidity = weather.humidity;

    if (temp < 10) {
      recommendations.push("‚ùÑÔ∏è –ù–∏–∑—å–∫–∞ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: –ó–∞—Ö–∏—Å—Ç—ñ—Ç—å –º–æ–ª–æ–¥—ñ —Ä–æ—Å–ª–∏–Ω–∏ –≤—ñ–¥ –∑–∞–º–æ—Ä–æ–∑–∫—ñ–≤");
    }

    if (humidity < 40) {
      recommendations.push("üíß –ù–∏–∑—å–∫–∞ –≤–æ–ª–æ–≥—ñ—Å—Ç—å: –ó–±—ñ–ª—å—à—ñ—Ç—å –ø–æ–ª–∏–≤");
    } else if (humidity > 80) {
      recommendations.push("üåßÔ∏è –í–∏—Å–æ–∫–∞ –≤–æ–ª–æ–≥—ñ—Å—Ç—å: –†–∏–∑–∏–∫ –≥—Ä–∏–±–∫–æ–≤–∏—Ö –∑–∞—Ö–≤–æ—Ä—é–≤–∞–Ω—å");
    }

    if (temp > 25 && humidity < 60) {
      recommendations.push("‚òÄÔ∏è –°–ø–µ–∫–æ—Ç–Ω–∞ —ñ —Å—É—Ö–∞ –ø–æ–≥–æ–¥–∞: –Ü–Ω—Ç–µ–Ω—Å–∏–≤–Ω–µ –∑—Ä–æ—à–µ–Ω–Ω—è");
    }

    return recommendations;
  };

  const fetchWeatherData = async () => {
    if (!city) return;
    
    setLoading(true);
    setError('');
    
    try {
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${city}`);
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${city}`);
      
      if (!weatherResponse.ok || !forecastResponse.ok) {
        throw new Error('–ü–æ–º–∏–ª–∫–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö');
      }
      
      const weatherData = await weatherResponse.json();
      const forecastData = await forecastResponse.json();
      
      setCurrentWeather(weatherData);
      setForecast(forecastData);
    } catch (err) {
      console.error('–ü–æ–º–∏–ª–∫–∞:', err);
      setError(err.message || '–ü–æ–º–∏–ª–∫–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö –ø–æ–≥–æ–¥–∏');
    } finally {
      setLoading(false);
    }
  };

  const agronomicRecommendations = currentWeather 
    ? getAgronomicRecommendations(currentWeather) 
    : [];

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', gap: 2 }}>
            <TextField
              label="–í–≤–µ–¥—ñ—Ç—å –º—ñ—Å—Ç–æ"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              fullWidth
            />
            <Button 
              variant="contained" 
              onClick={fetchWeatherData}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : '–ü–æ—à—É–∫'}
            </Button>
          </Paper>
        </Grid>

        {currentWeather && (
          <>
            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="h6">–ü–æ—Ç–æ—á–Ω–∞ –ø–æ–≥–æ–¥–∞</Typography>
                <Typography>
                  –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: {currentWeather.temperature}¬∞C
                </Typography>
                <Typography>
                  –í–æ–ª–æ–≥—ñ—Å—Ç—å: {currentWeather.humidity}%
                </Typography>
                <Typography>
                  –®–≤–∏–¥–∫—ñ—Å—Ç—å –≤—ñ—Ç—Ä—É: {currentWeather.windSpeed} –∫–º/–≥–æ–¥
                </Typography>
              </Paper>
            </Grid>

            <Grid item xs={12} md={6}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="h6">–ê–≥—Ä–æ–Ω–æ–º—ñ—á–Ω—ñ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ—ó</Typography>
                {agronomicRecommendations.length > 0 ? (
                  agronomicRecommendations.map((rec, index) => (
                    <Typography key={index} variant="body2">
                      {rec}
                    </Typography>
                  ))
                ) : (
                  <Typography variant="body2">–ù–µ–º–∞—î —Å–ø–µ—Ü—ñ–∞–ª—å–Ω–∏—Ö —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ–π</Typography>
                )}
              </Paper>
            </Grid>

            <Grid item xs={12}>
              <Paper sx={{ p: 2 }}>
                <Typography variant="h6">–ü—Ä–æ–≥–Ω–æ–∑ –ø–æ–≥–æ–¥–∏</Typography>
                <Box sx={{ width: '100%', height: 300 }}>
                  <LineChart
                    width={800}
                    height={300}
                    data={forecast}
                    margin={{
                      top: 5,
                      right: 30,
                      left: 20,
                      bottom: 5,
                    }}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis 
                      dataKey="date" 
                      tickFormatter={(date) => new Date(date).toLocaleDateString()}
                    />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line 
                      type="monotone" 
                      dataKey="temperature" 
                      stroke="#8884d8" 
                      name="–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞"
                    />
                    <Line 
                      type="monotone" 
                      dataKey="humidity" 
                      stroke="#82ca9d" 
                      name="–í–æ–ª–æ–≥—ñ—Å—Ç—å"
                    />
                  </LineChart>
                </Box>
              </Paper>
            </Grid>
          </>
        )}
      </Grid>
    </Container>
  );
}

export default Dashboard;
