import React from 'react';
import { SunIcon, CloudIcon, CloudRainIcon, SnowflakeIcon } from 'lucide-react';

const dayNames = {
  'понеділок': 'Понеділок',
  'вівторок': 'Вівторок', 
  'середа': 'Середа',
  'четвер': 'Четвер',
  'п\'ятниця': 'П\'ятниця',
  'субота': 'Субота',
  'неділя': 'Неділя'
};

const getLocalizedDayName = (dayName) => {
  const lowerDayName = dayName.toLowerCase();
  return dayNames[lowerDayName] || dayName;
};

const WeatherMiniCard = ({ 
  date, 
  minTemp, 
  maxTemp, 
  condition = 'cloudy' 
}) => {
  const getWeatherIcon = () => {
    switch(condition) {
      case 'sunny': 
        return <SunIcon size={40} color="#FFC107" />;
      case 'cloudy': 
        return <CloudIcon size={40} color="#9E9E9E" />;
      case 'rainy':
        return <CloudRainIcon size={40} color="#2196F3" />;
      case 'snowy':
        return <SnowflakeIcon size={40} color="#03A9F4" />;
      default: 
        return <CloudIcon size={40} color="#9E9E9E" />;
    }
  };

  const localizedDate = getLocalizedDayName(date);

  return (
    <div className="w-[200px] border rounded-lg shadow-md bg-gray-100">
      <div className="flex flex-col items-center p-1.5">
        <h2 className="text-sm font-bold mb-1">
          {localizedDate}
        </h2>
        
        <div className="flex items-center justify-center gap-2 my-1">
          {getWeatherIcon()}
          <div className="flex flex-col items-center">
            <span className="text-xs text-gray-500">
              Мін. {minTemp}°
            </span>
            <span className="text-xs font-bold">
              Макс. {maxTemp}°
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WeatherMiniCard;
