import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Typography, 
  Box,
  TextField,
  Button,
  CircularProgress,
  Card,
  CardContent,
  CardHeader,
  Avatar,
  IconButton,
  Chip
} from '@mui/material';
import { 
  LineChart, 
  Line, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend 
} from 'recharts';
import { 
  LocationOn as LocationIcon, 
  AutoGraph as GraphIcon,
  Refresh as RefreshIcon,
  WaterDropOutlined as WaterDropIcon,
  AirOutlined as WindIcon,
  DeviceThermostatOutlined as TemperatureIcon
} from '@mui/icons-material';

// –ö–æ–ª—å–æ—Ä–æ–≤–∞ –ø–∞–ª—ñ—Ç—Ä–∞, –Ω–∞—Ç—Ö–Ω–µ–Ω–Ω–∞ Berry Theme
const berryTheme = {
  primary: '#5e35b1',     // –ì–ª–∏–±–æ–∫–∏–π —Ñ—ñ–æ–ª–µ—Ç–æ–≤–∏–π
  secondary: '#3f51b5',   // –°–∏–Ω—ñ–π
  background: '#f5f5f5',  // –°–≤—ñ—Ç–ª–æ-—Å—ñ—Ä–∏–π
  paper: '#ffffff',       // –ë—ñ–ª–∏–π
  text: '#212121'         // –ú–∞–π–∂–µ —á–æ—Ä–Ω–∏–π
};

function Dashboard() {
  const [city, setCity] = useState('–ö–∏—ó–≤');
  const [searchCity, setSearchCity] = useState('–ö–∏—ó–≤');
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [geolocationStatus, setGeolocationStatus] = useState('');

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è –≤–∏–∑–Ω–∞—á–µ–Ω–Ω—è –º—ñ—Å—Ç–∞ –∑–∞ –≥–µ–æ–ª–æ–∫–∞—Ü—ñ—î—é
  const detectLocation = () => {
    if ("geolocation" in navigator) {
      setGeolocationStatus('–í–∏–∑–Ω–∞—á–∞—î–º–æ –≥–µ–æ–ª–æ–∫–∞—Ü—ñ—é...');
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          try {
            // –í–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –∑–æ–≤–Ω—ñ—à–Ω—å–æ–≥–æ API –¥–ª—è reverse geocoding
            const response = await fetch(
              `https://nominatim.openstreetmap.org/reverse?format=json&lat=${position.coords.latitude}&lon=${position.coords.longitude}&zoom=10&addressdetails=1`
            );
            const data = await response.json();
            
            // Extracting city name
            const detectedCity = 
              data.address.city || 
              data.address.town || 
              data.address.village || 
              '–ö–∏—ó–≤';

            setCity(detectedCity);
            setSearchCity(detectedCity);
            setGeolocationStatus(`–í–∏–∑–Ω–∞—á–µ–Ω–æ –º—ñ—Å—Ç–æ: ${detectedCity}`);
          } catch (err) {
            console.error('–ü–æ–º–∏–ª–∫–∞ –≤–∏–∑–Ω–∞—á–µ–Ω–Ω—è –º—ñ—Å—Ç–∞:', err);
            setGeolocationStatus('–ù–µ –≤–¥–∞–ª–æ—Å—è –≤–∏–∑–Ω–∞—á–∏—Ç–∏ –º—ñ—Å—Ç–æ');
          }
        },
        (error) => {
          console.error('–ü–æ–º–∏–ª–∫–∞ –≥–µ–æ–ª–æ–∫–∞—Ü—ñ—ó:', error);
          setGeolocationStatus('–ì–µ–æ–ª–æ–∫–∞—Ü—ñ—è –Ω–µ –ø—ñ–¥—Ç—Ä–∏–º—É—î—Ç—å—Å—è');
        }
      );
    } else {
      setGeolocationStatus('–ì–µ–æ–ª–æ–∫–∞—Ü—ñ—è –Ω–µ –ø—ñ–¥—Ç—Ä–∏–º—É—î—Ç—å—Å—è');
    }
  };

  // –ê–≥—Ä–æ–Ω–æ–º—ñ—á–Ω—ñ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ—ó –Ω–∞ –æ—Å–Ω–æ–≤—ñ –ø–æ–≥–æ–¥–Ω–∏—Ö –¥–∞–Ω–∏—Ö
  const getAgronomicRecommendations = (weather) => {
    if (!weather) return [];

    const recommendations = [];
    const temp = weather.temperature;
    const humidity = weather.humidity;

    if (temp < 10) {
      recommendations.push("‚ùÑÔ∏è –ù–∏–∑—å–∫–∞ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: –ó–∞—Ö–∏—Å—Ç—ñ—Ç—å –º–æ–ª–æ–¥—ñ —Ä–æ—Å–ª–∏–Ω–∏ –≤—ñ–¥ –∑–∞–º–æ—Ä–æ–∑–∫—ñ–≤");
    }

    if (humidity < 40) {
      recommendations.push("üíß –ù–∏–∑—å–∫–∞ –≤–æ–ª–æ–≥—ñ—Å—Ç—å: –ó–±—ñ–ª—å—à—ñ—Ç—å –ø–æ–ª–∏–≤");
    } else if (humidity > 80) {
      recommendations.push("üåßÔ∏è –í–∏—Å–æ–∫–∞ –≤–æ–ª–æ–≥—ñ—Å—Ç—å: –†–∏–∑–∏–∫ –≥—Ä–∏–±–∫–æ–≤–∏—Ö –∑–∞—Ö–≤–æ—Ä—é–≤–∞–Ω—å");
    }

    if (temp > 25 && humidity < 60) {
      recommendations.push("‚òÄÔ∏è –°–ø–µ–∫–æ—Ç–Ω–∞ —ñ —Å—É—Ö–∞ –ø–æ–≥–æ–¥–∞: –Ü–Ω—Ç–µ–Ω—Å–∏–≤–Ω–µ –∑—Ä–æ—à–µ–Ω–Ω—è");
    }

    return recommendations;
  };

  const fetchWeatherData = async () => {
    setLoading(true);
    setError('');
    
    try {
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${searchCity}`);
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${searchCity}`);
      
      if (!weatherResponse.ok || !forecastResponse.ok) {
        throw new Error('–ü–æ–º–∏–ª–∫–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö');
      }
      
      const weatherData = await weatherResponse.json();
      const forecastData = await forecastResponse.json();
      
      setCurrentWeather(weatherData);
      setForecast(forecastData);
      setCity(searchCity);
      setLoading(false);
    } catch (err) {
      console.error('–ü–æ–º–∏–ª–∫–∞:', err);
      setError(err.message || '–ü–æ–º–∏–ª–∫–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –¥–∞–Ω–∏—Ö –ø–æ–≥–æ–¥–∏');
      setLoading(false);
    }
  };

  // –í–∏–∫–ª–∏–∫ –≥–µ–æ–ª–æ–∫–∞—Ü—ñ—ó —Ç–∞ –æ—Ç—Ä–∏–º–∞–Ω–Ω—è –ø–æ–≥–æ–¥–∏ –ø—Ä–∏ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—ñ
  useEffect(() => {
    // –°–ø–æ—á–∞—Ç–∫—É –≤–∏–∫–ª–∏–∫–∞—î–º–æ –≥–µ–æ–ª–æ–∫–∞—Ü—ñ—é
    detectLocation();
    
    // –ü–æ—Ç—ñ–º –æ—Ç—Ä–∏–º—É—î–º–æ –ø–æ–≥–æ–¥—É 
    fetchWeatherData();
  }, []);

  const agronomicRecommendations = currentWeather 
    ? getAgronomicRecommendations(currentWeather) 
    : [];

  return (
    <Container 
      maxWidth="lg" 
      sx={{ 
        mt: 4, 
        mb: 4, 
        backgroundColor: berryTheme.background,
        minHeight: '100vh',
        p: { xs: 1, md: 3 }
      }}
    >
      <Grid container spacing={3}>
        {/* –ü–æ–∫—Ä–∞—â–µ–Ω–∏–π –ø–æ—à—É–∫–æ–≤–∏–π –±–ª–æ–∫ */}
        <Grid item xs={12}>
          <Card 
            elevation={6} 
            sx={{ 
              backgroundColor: berryTheme.paper,
              borderRadius: 2 
            }}
          >
            <CardHeader
              avatar={
                <Avatar sx={{ bgcolor: berryTheme.primary }}>
                  <LocationIcon />
                </Avatar>
              }
              action={
                <IconButton onClick={detectLocation} color="primary">
                  <RefreshIcon />
                </IconButton>
              }
              title="–ü–æ—à—É–∫ –º—ñ—Å—Ç–∞"
              subheader={geolocationStatus}
            />
            <CardContent>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <TextField
                  label="–í–≤–µ–¥—ñ—Ç—å –º—ñ—Å—Ç–æ"
                  value={searchCity}
                  onChange={(e) => setSearchCity(e.target.value)}
                  fullWidth
                  variant="outlined"
                  color="secondary"
                />
                <Button 
                  variant="contained" 
                  color="primary"
                  onClick={fetchWeatherData}
                  disabled={loading}
                  sx={{ 
                    backgroundColor: berryTheme.primary,
                    '&:hover': { backgroundColor: berryTheme.secondary }
                  }}
                >
                  {loading ? <CircularProgress size={24} /> : '–ü–æ—à—É–∫'}
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {loading ? (
          <Grid item xs={12} display="flex" justifyContent="center">
            <CircularProgress color="primary" />
          </Grid>
        ) : error ? (
          <Grid item xs={12}>
            <Card 
              elevation={6} 
              sx={{ 
                bgcolor: 'error.light',
                borderRadius: 2
              }}
            >
              <CardContent>
                <Typography color="error">{error}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ) : currentWeather && (
          <>
            <Grid item xs={12} md={6}>
              <Card 
                elevation={6} 
                sx={{ 
                  height: '100%',
                  backgroundColor: berryTheme.paper,
                  borderRadius: 2 
                }}
              >
                <CardHeader
                  avatar={
                    <Avatar sx={{ bgcolor: berryTheme.secondary }}>
                      <LocationIcon />
                    </Avatar>
                  }
                  title={`–ü–æ—Ç–æ—á–Ω–∞ –ø–æ–≥–æ–¥–∞ –≤ ${city}`}
                />
                <CardContent>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={4} display="flex" alignItems="center">
                      <TemperatureIcon color="primary" sx={{ mr: 1 }} />
                      <Typography>
                        –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞: {currentWeather.temperature}¬∞C
                      </Typography>
                    </Grid>
                    <Grid item xs={12} sm={4} display="flex" alignItems="center">
                      <WaterDropIcon color="primary" sx={{ mr: 1 }} />
                      <Typography>
                        –í–æ–ª–æ–≥—ñ—Å—Ç—å: {currentWeather.humidity}%
                      </Typography>
                    </Grid>
                    <Grid item xs={12} sm={4} display="flex" alignItems="center">
                      <WindIcon color="primary" sx={{ mr: 1 }} />
                      <Typography>
                        –®–≤–∏–¥–∫—ñ—Å—Ç—å –≤—ñ—Ç—Ä—É: {currentWeather.windSpeed} –∫–º/–≥–æ–¥
                      </Typography>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card 
                elevation={6} 
                sx={{ 
                  height: '100%',
                  backgroundColor: berryTheme.paper,
                  borderRadius: 2 
                }}
              >
                <CardHeader
                  avatar={
                    <Avatar sx={{ bgcolor: berryTheme.primary }}>
                      <GraphIcon />
                    </Avatar>
                  }
                  title="–ê–≥—Ä–æ–Ω–æ–º—ñ—á–Ω—ñ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ—ó"
                />
                <CardContent>
                  {agronomicRecommendations.length > 0 ? (
                    agronomicRecommendations.map((rec, index) => (
                      <Chip 
                        key={index} 
                        label={rec} 
                        color="primary" 
                        variant="outlined" 
                        sx={{ m: 0.5 }}
                      />
                    ))
                  ) : (
                    <Typography variant="body2">–ù–µ–º–∞—î —Å–ø–µ—Ü—ñ–∞–ª—å–Ω–∏—Ö —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü—ñ–π</Typography>
                  )}
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12}>
              <Card 
                elevation={6} 
                sx={{ 
                  backgroundColor: berryTheme.paper,
                  borderRadius: 2 
                }}
              >
                <CardHeader
                  avatar={
                    <Avatar sx={{ bgcolor: berryTheme.secondary }}>
                      <GraphIcon />
                    </Avatar>
                  }
                  title="–ü—Ä–æ–≥–Ω–æ–∑ –ø–æ–≥–æ–¥–∏"
                />
                <CardContent>
                  <Box sx={{ width: '100%', height: 300 }}>
                    <LineChart
                      width={800}
                      height={300}
                      data={forecast}
                      margin={{
                        top: 5,
                        right: 30,
                        left: 20,
                        bottom: 5,
                      }}
                    >
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis 
                        dataKey="date" 
                        tickFormatter={(date) => new Date(date).toLocaleDateString()}
                      />
                      <YAxis />
                      <Tooltip 
                        contentStyle={{ 
                          backgroundColor: berryTheme.background,
                          borderColor: berryTheme.primary 
                        }}
                      />
                      <Legend />
                      <Line 
                        type="monotone" 
                        dataKey="temperature" 
                        stroke={berryTheme.primary} 
                        name="–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞"
                      />
                      <Line 
                        type="monotone" 
                        dataKey="humidity" 
                        stroke={berryTheme.secondary} 
                        name="–í–æ–ª–æ–≥—ñ—Å—Ç—å"
                      />
                    </LineChart>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </>
        )}
      </Grid>
    </Container>
  );
}

export default Dashboard;
