import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  TextField,
  Button,
  Box,
  CircularProgress
} from '@mui/material';
import { 
  WbSunny as SunIcon, 
  Cloud as CloudIcon, 
  AcUnit as SnowIcon, 
  WaterDropOutlined as RainIcon 
} from '@mui/icons-material';

function WeatherDashboard() {
  const [city, setCity] = useState('Київ');
  const [originalCityName, setOriginalCityName] = useState('Київ'); 
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Форматування дати з повною назвою дня, числом та місяцем
  const formatFullDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('uk-UA', {
      weekday: 'long',
      day: 'numeric',
      month: 'long'
    });
  };

  // Маппінг умов погоди до іконок
  const mapConditionToIcon = (description) => {
    const lowerDescription = description.toLowerCase();
    
    if (lowerDescription.includes('clear') || lowerDescription.includes('sunny')) {
      return <SunIcon fontSize="large" />;
    }
    if (lowerDescription.includes('cloud') || lowerDescription.includes('overcast')) {
      return <CloudIcon fontSize="large" />;
    }
    if (lowerDescription.includes('snow')) {
      return <SnowIcon fontSize="large" />;
    }
    if (lowerDescription.includes('rain') || lowerDescription.includes('drizzle')) {
      return <RainIcon fontSize="large" />;
    }
    return <CloudIcon fontSize="large" />;
  };

  const fetchWeatherData = async () => {
    if (!city) return;
    
    setOriginalCityName(city);
    setLoading(true);
    setError('');
    
    try {
      const weatherResponse = await fetch(`http://localhost:5000/api/weather/current/${city}`);
      
      if (!weatherResponse.ok) {
        const errorData = await weatherResponse.json();
        throw new Error(errorData.error || 'Помилка отримання даних про погоду');
      }
      
      const weatherData = await weatherResponse.json();
      
      const forecastResponse = await fetch(`http://localhost:5000/api/weather/forecast/${city}`);
      
      if (!forecastResponse.ok) {
        const errorData = await forecastResponse.json();
        throw new Error(errorData.error || 'Помилка отримання прогнозу погоди');
      }
      
      const forecastData = await forecastResponse.json();
      
      // Додаємо форматування дати та іконки до прогнозу
      const formattedForecast = forecastData.map(day => ({
        ...day,
        fullDate: formatFullDate(day.date),
        icon: mapConditionToIcon(day.description)
      }));
      
      setCurrentWeather(weatherData);
      setForecast(formattedForecast);
    } catch (err) {
      console.error('Помилка:', err);
      setError(err.message || 'Помилка отримання даних погоди');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWeatherData();
  }, []);

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Grid container spacing={3}>
        {/* Пошук міста */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2, display: 'flex', gap: 2 }}>
            <TextField
              label="Введіть місто"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              fullWidth
            />
            <Button 
              variant="contained" 
              onClick={fetchWeatherData}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Пошук'}
            </Button>
          </Paper>
        </Grid>

        {/* Повідомлення про помилку */}
        {error && (
          <Grid item xs={12}>
            <Paper sx={{ p: 2, bgcolor: 'error.light' }}>
              <Typography color="error">{error}</Typography>
            </Paper>
          </Grid>
        )}

        {/* Поточна погода */}
        {currentWeather && (
          <>
            {/* Основні метрики */}
            <Grid item xs={12} container spacing={2}>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Box>
                    <Typography variant="h6">Температура</Typography>
                    <Typography variant="h4">{currentWeather.temperature}°C</Typography>
                  </Box>
                  {mapConditionToIcon(currentWeather.description)}
                </Paper>
              </Grid>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Box>
                    <Typography variant="h6">Вологість</Typography>
                    <Typography variant="h4">{currentWeather.humidity}%</Typography>
                  </Box>
                  <CloudIcon fontSize="large" />
                </Paper>
              </Grid>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Box>
                    <Typography variant="h6">Вітер</Typography>
                    <Typography variant="h4">{currentWeather.windSpeed} км/год</Typography>
                  </Box>
                  <CloudIcon fontSize="large" />
                </Paper>
              </Grid>
            </Grid>

            {/* Прогноз на наступні дні */}
            <Grid item xs={12}>
              <Typography variant="h5" sx={{ my: 2 }}>
                Прогноз на тиждень
              </Typography>
              <Grid container spacing={2}>
                {forecast.slice(0, 7).map((day, index) => (
                  <Grid item xs={6} sm={4} md={2} key={index}>
                    <Paper 
                      sx={{ 
                        p: 2, 
                        textAlign: 'center',
                        transition: 'transform 0.3s',
                        '&:hover': { transform: 'scale(1.05)' }
                      }}
                    >
                      <Typography variant="subtitle2" sx={{ fontWeight: 'bold' }}>
                        {day.fullDate}
                      </Typography>
                      <Box my={2}>
                        {day.icon}
                      </Box>
                      <Typography variant="body1">
                        Мін. {day.minTemperature}°C / Макс. {day.maxTemperature}°C
                      </Typography>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </>
        )}
      </Grid>
    </Container>
  );
}

export default WeatherDashboard;
